,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    p.NAZWA,',
'    p.CENA,',
'    p.OPIS,',
'    --COALESCE(h.GATUNEK, ''Brak danych'') AS GATUNEK,',
'    h.GATUNEK,',
'    h.KRAJ_POCH,',
'    --COALESCE(h.TEMP_PARZ, -1) AS TEMP_PARZ,',
'    h.ILE_PARZEN,',
'    h.TEMP_PARZ,',
'    h.CZAS_PARZ,',
'    h.AROMATYCZNOSC,',
'    --COALESCE(h.MOC, -1) AS MOC,',
'    h.MOC,',
'    (',
'        SELECT LISTAGG(prom.nazwa_promocji, '', '') WITHIN GROUP (ORDER BY prom.nazwa_promocji)',
'        FROM produkt_na_promocji prom',
'        LEFT JOIN promocja pro',
'        ON prom.nazwa_promocji = pro.nazwa_promocji',
'        WHERE pro.data_od < CURRENT_DATE AND pro.data_do > CURRENT_DATE',
'        AND prom.nazwa_produktu = p.nazwa',
'    ) AS PROMOCJE,',
'    (',
'        SELECT LISTAGG(tag_nazwa, '', '') WITHIN GROUP (ORDER BY tag_nazwa)',
'        FROM herbata_tag t',
'        WHERE t.nazwa_produktu = p.NAZWA',
'    ) AS TAGI',
'FROM ',
'    produkt p ',
'LEFT JOIN ',
'    herb_rodzaj h ',
'ON ',
'    p.NAZWA = h.NAZWA_PRODUKTU',
'WHERE ',
'    p.KATEGORIA_NAZWA = ''herbata'';',
''))
